(window.webpackJsonp=window.webpackJsonp||[]).push([[44],{411:function(v,_,p){"use strict";p.r(_);var t=p(3),e=Object(t.a)({},function(){var v=this,_=v.$createElement,p=v._self._c||_;return p("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[p("h2",{attrs:{id:"新环境，新开始"}},[p("a",{staticClass:"header-anchor",attrs:{href:"#新环境，新开始","aria-hidden":"true"}},[v._v("#")]),v._v(" 新环境，新开始")]),v._v(" "),p("p",[v._v("人在走着走着的过程中发现了自己的欢喜。")]),v._v(" "),p("p",[v._v("加入星球有一短时间了，之前也断断续续分享过几篇文章，一直没有持续，不过，每天都进来看看，真的见证了很多球友的成长，不得不说，你们真的厉害。")]),v._v(" "),p("p",[v._v("今天第一天来新公司报道，一个新的环境，一个新的开始，所以想通过一篇文章记录下来，记录自己的改变，记录自己的成长。")]),v._v(" "),p("p",[v._v("虽然说是第一天来新公司报道，但是也是昨天刚从老的公司离职中间没有任何一天的休息，即使是这样，我依然保持着很高的期待。")]),v._v(" "),p("p",[v._v("因为前天看到粥老师说到关于选择的话题，非常有共鸣，刚好趁着这次换工作的思考总结说说自己的感受。")]),v._v(" "),p("p",[v._v("1.为什么换工作")]),v._v(" "),p("p",[v._v("最近半年由于自己关注了很多圈子，阅读了大量公众号，专栏，书籍，自己的认识与思维发生了很大的转变。简直是醍醐，从未像现在一样认识到自己的愚昧，也对现在工作了近 3 年的工作进行了梳理思考总结，重新审视自己，最终决定换个环境找到更适合自己，更好的发展的平台。")]),v._v(" "),p("p",[v._v("2.关于选择")]),v._v(" "),p("p",[v._v("我是个彻彻底底的学渣，一个专科毕业生，然而以前我并没有意识到自己是一个学渣，反而觉得自己很牛逼的样子，高中的时候就一直想辍学，最后在家人的再三劝阻下没有成功，最后上了一所专科学校，但是我依然没有意识到人生的差距在哪里，我的专科三年全都送给了网吧，几乎没有上过一天完整的课，唯独有一点就是我想控制自己的人生，也可能就是这一点，才让我走到今天，在走了这么多弯路之后，才幡然醒悟。")]),v._v(" "),p("p",[v._v("大学期间做过服务员，摆过地摊，但是认知上并没有提升，毕业之后，进了一家工厂白班 12 个小时， 晚班 12 个小时就这样循环的重复着，直到第 8 个月，终于再也忍不住了，这不是我想要的人生，我似乎都能看到我 30 年以后的样子。")]),v._v(" "),p("p",[v._v("离职之后，我就思考我该去哪，该做什么。")]),v._v(" "),p("p",[v._v("其实，那个时候，我只看到了别人的成功，没有看到别人背后的付出，这其实会导致一个人自负的。")]),v._v(" "),p("p",[v._v("3.关于城市")]),v._v(" "),p("p",[v._v("出来之后，我就想这次要去繁荣一些的地方，人多的地方，人人都说北上广深是创造梦想的地方，每个年轻人都应该去创出自己的一番事业，但是自己通过一些思量之后最终选择了离自己非常近的省会城市，相对来说有一定的繁荣，至于最后为什么没有选择去一线城市，其中的缘由就不过多解释，总的来说人一定要去相对来说更高的地方去有所追求。")]),v._v(" "),p("p",[v._v("4.关于行业")]),v._v(" "),p("p",[v._v("真的很庆幸的是，在多次转折之后最终走到了互联网的行业，这应该是最正确的一次选择了，因为互联网给我们带来的变化是巨大的，机会也是到处存在，因为互联网的存在我看到了更多草根的逆袭成功，如果不是在互联网真的很难在看到逆袭的成功者。")]),v._v(" "),p("p",[v._v("自己走到互联网这个行业，真的是机缘巧合。")]),v._v(" "),p("p",[v._v("在选择来到省会开始时，找到了一个淘宝的运行客服工作，每天就是打理打理店铺的一些上新和回答客户的一些问题，当时的工资一个月不到 2000 块，我们公司有一个做美工的同事，每天做一些图片供我们使用，有一次聊到了薪资的事情，我问他你的工资大概是多少，他告诉我说是 4000 多吧。")]),v._v(" "),p("p",[v._v("我当时就懵逼了，卧槽这么高，随即，我就回家查了一些资料怎么学习这些技术，当时也是培训机构刚火热的时候，随后我就果断的辞职选择了在培训机学了 UI 这门课程，在随后一个机会下我又转到了前端开发的岗位，现在已经做前端 2 年了，薪资也从当年的 1000+，增加了一位数。")]),v._v(" "),p("p",[v._v("真的很庆幸，自己选择了一个好的行业，如果当时我留在工厂里，想想都觉得可怕。")]),v._v(" "),p("p",[v._v("5.我有努力到自己认同吗？")]),v._v(" "),p("p",[v._v("在培训机构学习时，确实很认真，起码从上学开始就没有这么认真过，为了从机构毕业之后能有一个好的工作，有一段时间每天学到很晚甚至是凌晨 2 点，毕业之后我也如愿了找到了工作，平时加班也挺多，我其实对加班一点不都反感，相反我觉得能够学到更多东西挺好，但是那些无意义的加班，就不好了，但是好像仅此而已，我并没有充分利用我的业余时间，去充实自己，去更好的提升自己。")]),v._v(" "),p("p",[v._v("我也竟然一直无知的以为我挺努力，从加入一些圈子从那些牛人的身上看到了一些事情之后，比如粥老师，我才发现我竟然骗了自己这么久，我那哪能叫努力，分明是在掩盖逃避自己的懒惰。")]),v._v(" "),p("p",[v._v("6.找到了自己的欢喜坚持下去")]),v._v(" "),p("p",[v._v("认识到自身的一些问题之后，也充分了利用自己的业余时间，在提升自己专业技能的同时，也开了一些有意义，有复利的事情，也开始了坚持写作，在坚持了几个月的技术博客写作之后，发现自己渐渐的喜欢上了写作，每天都想表达出一些什么，虽然写作这件事我还不擅长，但是，我想把它坚持下去，向优秀的球友们一起学习。")])])},[],!1,null,null,null);_.default=e.exports}}]);