(window.webpackJsonp=window.webpackJsonp||[]).push([[60],{438:function(v,_,t){"use strict";t.r(_);var p=t(3),r=Object(p.a)({},function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h2",{attrs:{id:"我是如何自学前端的，应该如何入门"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#我是如何自学前端的，应该如何入门","aria-hidden":"true"}},[v._v("#")]),v._v(" 我是如何自学前端的，应该如何入门")]),v._v(" "),t("p",[v._v("自从更新了《我的Web 前端自学之路》之后，收到了很多读者的认可，也碰到了很多像我一样从零开始自学的朋友，并加微信进行了一些交流，很多读者都询问我，该如何自学，有什么好的建议。也有些人说感觉很枯燥，看书看到怀疑人生，该怎么做。")]),v._v(" "),t("p",[v._v("首先我也是个学习者，并不是什么技术大牛，我深知自己的技术能力是多少，可能经历比你们多一点而已，另外每个人自身基础的条件，所处的环境都不一样，我也无法给每个人具体的建议。")]),v._v(" "),t("p",[v._v("我不能在对你没有任何了解的情况下，给出一些不负责任的建议，大家都是成年人，应该懂得如何取舍。")]),v._v(" "),t("p",[v._v("今天就来说说我的自学方法，我的做法不一定适合每个人，你们可以参考借鉴。")]),v._v(" "),t("ol",[t("li",[v._v("有目标")])]),v._v(" "),t("p",[v._v("学习一定要有目标，目标不一定是很宏大的，其实目标可以很简单，比如：想拿高工资，想从事技术相关的工作，想有一技之长等等。")]),v._v(" "),t("p",[v._v("有目标就有动力，可以促进你很好的去行动，也会指引你前行。我学前端的目标就很简单，相对其他行业来说收入还不错，另外一个就是岗位需求也很多。")]),v._v(" "),t("p",[v._v("所以如果你想判别到底该不该进入某个行业，你不妨从这四个方面去考虑：关键能力，岗位价值，晋升标准，职业前景，大家可以多多思考。")]),v._v(" "),t("ol",{attrs:{start:"2"}},[t("li",[v._v("先了解熟悉")])]),v._v(" "),t("p",[v._v("如果你有目标也决定去做，那剩下的就是好好行动，很多人可能不知道，我其实是培训出身（至于什么机构，你们就别问了，容易让人误会我是打广告），当时学的是 UI 设计，大多都是教PS、AI、ID等工具的使用，当然了还传达一些设计的理念，哦对了，还有一段时间的手绘课程（我画的奇丑...不是这块料）。")]),v._v(" "),t("p",[v._v("不过其中有个模块是网页设计的课程，里面大概有三五天的 HTML+CSS 的课程，具体的几天已经记不清楚了。不过呢，很多学习设计的同学对这个很不感兴趣，也觉得这个很难，大多都放弃了。")]),v._v(" "),t("p",[v._v("我当时觉得这可能就是我能够找到工作的机会，因为别人都不愿意去学，所以我听的很认真，之后自己又通过 W3School 上的一些入门教程，算是对 HTML+CSS 有了简单的了解。有机会和你们说说我的一些编程故事，这里就不多说了。")]),v._v(" "),t("p",[v._v("所以我建议开始入门的同学不妨视频+资料结合的方式去学习，现在很多在线教育学习平台，为了吸引用户有很多的免费入门课程而且质量很高，比如慕课网。不像以前课程很少。")]),v._v(" "),t("p",[v._v("通过视频课程有老师指导，然后在看一些资料进行强化一下，可能比你自己学习的更加有效理解起来更轻松一些。当然了也有人不喜欢看视频就喜欢自己学，那就另当别论了。")]),v._v(" "),t("p",[v._v("有很多从零开始入门的同学，上来就开始看文档，看的非常烦躁，很多东西也没听过，没见过，不好理解，看了一边之后又记不住，其实不是你记不住，我也记不住，没人能记住，你需要掌握方法，前期只要你能了解，熟悉就行。比如你能知道画个边框背景色的矩形，要用到border，background 这些属性这就行了，至于不会写你可以再去查文档，多写几次自然就能记住。")]),v._v(" "),t("ol",{attrs:{start:"3"}},[t("li",[v._v("边做边学")])]),v._v(" "),t("p",[v._v("在了解和熟悉之后，你就可以尝试去做一个东西了，比如自己喜欢个某个网站去模仿下，找一个自己感觉比较好的设计图自己实现一下。为什么要边做边学？我认为有两点好处。")]),v._v(" "),t("p",[v._v("第一：在做项目的过程中会遇到很多问题，你需要去主动检索然后解决，这种检索记忆的方式是比较高效的，不信你想想是不是当你遇到一些困难时，然后找到了解决办法，这个办法你会记很久。")]),v._v(" "),t("p",[v._v("第二：当你解决问题之后会非常的有成就感，然后通过这种刺激你又会不断的学习，另外通过解决问题的方式去学习是非常有趣的，因为它能实实在在的做出东西，而不是面对一推枯燥的代码，很反感。")]),v._v(" "),t("p",[v._v("所以当你了解熟悉之后，不妨试试边做边学，它会给你带来快乐和动力，我在刚接触 HTML+CSS 的时候就找了两个比较喜欢的网站去模仿，每天晚上都写到很晚，有一段时间都是2，3点才睡，第二天又起来上课，其实不是我努力，而是我忘记了睡觉。")]),v._v(" "),t("p",[v._v("因为当在想着如何解决问题的时候，你会很专注。包括现在也是，在一个功能未实现之前我啥都不想做，就想一个人静静的写代码，想思路。")]),v._v(" "),t("p",[v._v("恰好也正是因为这两个网站，我有了第一份初级的工作，别人有设计作品，而我有设计还能用代码实现出来，所以我们两个同时去面试却选择了我，这就是优势。于是我就开始了我第一份网页设计的工作，也为后面转前端做了铺垫，感觉自己非常的幸运。")]),v._v(" "),t("p",[v._v("以上都是我自己的一些学习方法，希望对你们能够有借鉴价值，不仅我刚开始入门是如此，包括后来的JavaScript，Angular，Vue等知识都是这种方式去学习。")]),v._v(" "),t("p",[v._v("最后，一个小小的建议吧，我经常在知乎上看看有人提问说自己自学xxx，xxx，xxxx，xxx等等为什么不好找工作，貌似他什么都会，可是你拿什么证明你会？所以要拿出作品说话，而且你要善于利用互联网，把作品放到网上，这样可以让别人随时看到，只要你有实力，还怕找不到工作吗？")]),v._v(" "),t("p",[v._v("欢迎大家一起交流，最后作者码字不容易，而且建议良心，真诚，别忘了点赞哦。")]),v._v(" "),t("p",[v._v("关注微信公众号：六小登登，领取我一路走来的全套开发资源。")])])},[],!1,null,null,null);_.default=r.exports}}]);